import React, { Component, createContext } from 'react';
import './App.css';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom'
import Navigation from './components/Navbar/Navbar';
import Menu from './components/Navbar/Menu';
import HomePage from './Home/Home.js';
import Settings from './Settings/Settings';


class App extends React.Component{

constructor(props){
  super(props);
  this.state = {
    countrySearch:'usa',
    city:'Bangkok',
  }
}

updateState = (e) => {
  this.setState({
    countrySearch: e
  })

}

render(){
  return (
    <Router>
       <div className="App">

            <div className="navbar" id="mn_menu">
                <Navigation />
            </div>
            <Switch>

                <Route path='/Settings'>
                    <Settings countryChange={this.updateState}/>
                </Route>

                <Route path='/'>
                    <HomePage countryData={this.state.countrySearch} cityMeteo={this.state.city}/>
                </Route>

            </Switch>
      </div>

    </Router>
  );
}
}

export default App;
