import React, { useContext, createContext, useState } from "react";
import '../Settings.css';
import axios from 'axios';
const URL = 'http://localhost:3000/api';

export default class FirstName extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      prenom:'',
      nom:'',
      groupeClasse:'',
      etat:'',
    }
      this.handleChange = this.handleChange.bind(this);
  }

  newStudent(){
      return axios.post(URL, { nom, prenom, groupeClasse, etat })
  }

  stocker(e) {
    this.setState({prenom: e.target.value});
    this.setState({nom: e.target.value});
  }

  handleChange(e) {
    this.setState({groupeClasse: e.target.value});
    this.setState({etat: e.target.value});
  }

  render() {
  return (
    <div>
      <div className="app-row" style={{margin: 0, padding: 0}}>

        <div className="app-col-xs-12 app-col-md-6 app-col-lg-3">

              <a className="info">First Name</a>
              <input type="text" placeholder="Saisissez votre recherche" onChange={(e) => this.stocker(e)} value={this.state.prenom}/>

        </div>

        <div className="app-col-xs-12 app-col-md-6 app-col-lg-3">

              <a className="info">LastName</a>
              <input type="text" placeholder="Saisissez votre recherche" onChange={(e) => this.stocker(e)} value={this.state.nom}/>

        </div>

        <div className="app-col-xs-12 app-col-md-6 app-col-lg-3">

            <form >
              <a className="info">Groupe</a>
              <label>
                <select value={this.state.groupeClasse} onChange={(e) =>this.handleChange(e)}>
                  <option value="Vide"></option>
                  <option value="2A">2A</option>
                  <option value="2B">2B</option>
                  <option value="4A">4A</option>
                  <option value="4B">4B</option>
                </select>
              </label>
            </form>

        </div>
        <div className="app-col-xs-12 app-col-md-6 app-col-lg-3">

            <form>
              <a className="info">Statut</a>
              <label>
                <select value={this.state.etat} onChange={(e) =>this.handleChange(e)}>
                  <option value="Vide"></option>
                  <option value="Confirmed">Confirmed</option>
                  <option value="NotConfirmed">Not Confirmed</option>
                </select>
              </label>
              <input type="submit" value="Submit" onClick={newStudent()}/>
            </form>

        </div>
    </div>
  </div>

  );
}
}
