import React, { Component } from 'react';
import { iex } from './iex.js';
import { stock } from './stock.js';

const changedStyle = {

    color: '#4caf50',
    fontSize: '0.8rem',
    marginLeft: 5
}

class StockRow extends Component {

    constructor(props) {

        super(props)
        this.state = {
            price: null,
            date: null,
            time: null,
            dollar_change: null,
            percent_change: null  
        }
    }

    applyData(data) {

        this.setState({ 

            price: data.price.toFixed(2),
            date: data.date,
            time: data.time,
        });

        stock.getYerstedaysClose(this.props.ticker, data.date, (yersterday) => {

            const dollar_change = (data.price - yersterday.price).toFixed(2)
            const percent_change = (100 * dollar_change / yersterday.price).toFixed(2)

            this.setState({

                dollar_change: `$(dollar_change)`,
                percent_change: ` $(percent_change)`
            })
        })
    }

    componentDiMount() {

        stock.latestPrice(this.props.ticker, this.applyData.bind(this))
    }

    render() {

        return (

            <li className="list-group-item">
                <b>{this.props.ticker}</b> ${this.state.data.price}
                <span className="change" style={changedStyle}>
                {this.state.dollar_change}
                {this.state.percent_change}
                </span>
            </li>
        )    
    }
}

export default StockRow;
