import React, { PureComponent } from 'react';
import {ResponsiveContainer, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,} from 'recharts';
import './Graph2.css';

const DataFormater = (number) => {
    if(number > 1000000000){
      return (number/1000000000).toString() + 'B';
    }else if(number > 1000000){
      return (number/1000000).toString() + 'M';
    }else if(number > 1000){
      return (number/1000).toString() + 'K';
    }else{
      return number.toString();
    }
}


export default class Example extends PureComponent {
  static jsfiddleUrl = 'https://jsfiddle.net/alidingling/q4eonc12/';

    state = {
          data:[]
      };

      componentDidMount() {
          var url = `mongodb://localhost/CovidECE`;
          this.loadNews(url);
      }


      loadNews(url) {
          fetch(url)
              .then(response => response.json())
              .then(res => {
                  this.setState({data: res});
              });
      }

  render() {
    return (
      <div className="widgetGraph2">
        <h5>Class Data</h5>
            <ResponsiveContainer width="95%" height={350}>
              <BarChart width={500} height={300} data={this.state.data} margin={{ top: 20, right: 30, left: 20, bottom: 5,}}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="NC" stackId="a" fill="#8884d8" />
                  <Bar dataKey="CF" stackId="a" fill="#E53824" />
              </BarChart>
          </ResponsiveContainer>
    </div>
    );
  }
}
