import React, { Component } from 'react';
import { iex } from './iex.js';
import { stock } from './stock.js';

const changedStyle = {

    color: '#4caf50',
    fontSize: '0.8rem',
    marginLeft: 5
}

class StockRow extends Component {

    constructor(props) {

        super(props)
        this.state = {
            data: {
                dollar_change: 0,
                percent_change: 0   
            }

        }
    }

    applyData(data) {

        this.setState({ data: data })  
    }

    componentDiMount() {

        stock.latestPrice(this.props.ticker, this.applyData.bind(this))
    }

    render() {

        return (

            <li className="list-group-item">
                <b>{this.props.data.ticker}</b> ${this.state.data.price}
                <span className="change" style={changedStyle}>
                {this.state.data.dollar_change}
                {this.state.data.percent_change}
                </span>
            </li>
        )    
    }
}

export default StockRow;
