import React from 'react';
import DayCard from './Daycard';
import {useEffect, useState} from 'react';

export default (props) => {

  const[fullData, setFullData] = useState([])
  const[city, setCity] = useState(props.ville)

  const func = () => {
    const API_URL = "https://api.openweathermap.org/data/2.5/forecast/daily";
    const API_KEY = "4081444b7b90198136fefe6ed4ccf35b";
    const weatherURL = `${API_URL}?q=${this.props.ville}&cnt=4&units=metric&appid=${API_KEY}`

    fetch(weatherURL)
      .then(res => res.json())
      .then(data => {
      //const dailyData = data.list.filter(reading => reading.dt_txt.includes("18:00:00"))
      setFullData(data.list)
    })
  }

  const formatDayCards = () => {
    return fullData.map((reading, index) => <DayCard reading={reading} key={index} />)
  }

  useEffect(() => {
    func()
    setCity(props.ville)
  },[props.ville])

  return (
    <div className="container">
        <h5 className="display-5 text-muted">{city}</h5>
            <div className="row justify-content-center">
              {this.formatDayCards()}
            </div>
    </div>
  )
}
