  };

  componentDidMount() {
      var url = 'http://localhost:3000/api/';
      this.loadNews(url);
  }


loadNews(url) {
      axios.get(url)
          .then(response => {console.log(response)})
          .then(res => {
              this.setState({data: res});
              const longeur = res.data.length;
          });

