      // })
      // for(const key of this.state.mapDatas.keys()){console.log(key)}
  }

//insertNewStudent() {}
  render() {
    return (
      <div className="widgetGraph2">
        <h5>Class Data</h5>
            <ResponsiveContainer width="60%" height={350} >

            <PieChart height={300} data={data}>
              <Pie dataKey="value" data={data} fill="#8884d8" label />
            </PieChart>

          </ResponsiveContainer>
    </div>
    );
  }
}
