import React, { Component } from 'react';
import { iex } from './iex.js';
import { stock } from './stock.js';

const changedStyle = {

    color: '#4caf50',
    fontSize: '0.8rem',
    marginLeft: 5
}

class StockRow extends Component {

    constructor(props) {

        super(props)
        this.state = {
            price: null,
            date: null,
            time: null,
            dollar_change: null,
            percent_change: null  
        }
    }

    applyData(data) {

        this.setState({ 

            price: data.price,
            date: data.date,
            time: data.time,
        })  
    }

    componentDiMount() {

        stock.latestPrice(this.props.ticker, this.applyData.bind(this))
    }

    render() {

        return (

            <li className="list-group-item">
                <b>{this.props.ticker}</b> ${this.state.data.price}
                <span className="change" style={changedStyle}>
                {this.state.dollar_change}
                {this.state.percent_change}
                </span>
            </li>
        )    
    }
}

export default StockRow;
