import React, { useContext, createContext, useState } from "react";
import '../Settings.css';
import axios from 'axios';
const URL = 'http://localhost:3000/api';

export default class FirstName extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      prenom:'',
      nom:'',
      etat:'',
      id:''
    }
      this.stockerEtat = this.stockerEtat.bind(this);
  }

  newStudent(){
      return axios.post(URL, {nom: this.state.nom, prenom: this.state.prenom,etat: this.state.etat});
  }

  modifyStudent(){
      return axios.put(URL, {eleve_id: this.state.id, nom: this.state.nom, prenom: this.state.prenom,etat: this.state.etat});
  }

  deleteStudent(){
      return axios.delete(`http://localhost:3000/api/${this.state.id}`);
  }

  stockerPrenom(e) {
    this.setState({prenom: e.target.value});
  }

  stockerNom(e) {
    this.setState({nom: e.target.value});
  }

  stockerEtat(e) {
    this.setState({etat: e.target.value});
  }

  stockerId(e) {
    this.setState({id: e.target.value});
  }

