import React, { useContext, createContext, useState } from "react";
import '../Settings.css';
import axios from 'axios';
const URL = 'http://localhost:3000/api';

export default class FirstName extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      prenom:'',
      nom:'',
      classe:'',
      etat:'',
    }
      this.handleChangeGroupe = this.handleChangeGroupe.bind(this);
      this.handleChangeEtat = this.handleChangeEtat.bind(this);
  }

  newStudent(){
      return axios.post(URL, {nom: this.state.nom, prenom: this.state.prenom, classe: this.state.classe, etat: this.state.etat});
  }

  stockerPrenom(e) {
    this.setState({prenom: e.target.value});
  }

  stockerNom(e) {
    this.setState({nom: e.target.value});
  }

  handleChangeGroupe(e) {
    this.setState({classe: e.target.value});
  }

  handleChangeEtat(e) {
    this.setState({etat: e.target.value});
  }



