import React, { Component } from "react";
import './Home.css';
import widdgetBack from "../image/Home.jpg";
import down from "../image/down-chevron.png";
import homepage  from "../image/WidgetBack.jpg";

export default class Home extends Component {

  constructor(props) {
    super(props);
    this.state = {
        activeDiv: "HomePage",
    }
  }

  toggleDiv = () => {
      this.setState({
          activeDiv: this.state.activeDiv === "HomePage" ? "WidgetPage" : "HomePage",
      });
  };

  render() {
    return (
      <div className="Home">
              <div className={this.state.activeDiv === "HomePage" ? "HomePage show" : "HomePage hide"} id="HomePage">
                <img className="HomePage"src={homepage}/>
                <p className="Title">Welcome to the future<br />of Widgets</p>
                <img className="butn" type="button" onClick={this.toggleDiv} src={down}/>
              </div>
              <div className={this.state.activeDiv === "WidgetPage" ? "WidgetPage show" : "WidgetPage hide"} id="WidgetPage">
                <img className="WidgetPage" src={widdgetBack}/>
              </div>
      </div>
    );
  }
}
